import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditDestination = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [destination, setDestination] = useState({
    destinationName: "",
    district: "",
    province: "",
    category: "",
    keyHighlights: "",
  });

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/destinations/${id}`)
      .then((response) => {
        const data = response.data.destinations;
        setDestination({
          ...data,
          keyHighlights: data.keyHighlights.join(", "),
        });
      })
      .catch((error) => console.error("Error fetching destination:", error));
  }, [id]);

  const handleChange = (e) => {
    setDestination({ ...destination, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/destinations/${id}`, {
        ...destination,
        keyHighlights: destination.keyHighlights.split(","),
      });
      navigate("/");
    } catch (error) {
      console.error("Error updating destination:", error);
    }
  };

  return (
    <div>
      <h2>Edit Destination</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="destinationName"
          value={destination.destinationName}
          className="form-control mb-2"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="district"
          value={destination.district}
          className="form-control mb-2"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="province"
          value={destination.province}
          className="form-control mb-2"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="category"
          value={destination.category}
          className="form-control mb-2"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="keyHighlights"
          value={destination.keyHighlights}
          className="form-control mb-2"
          onChange={handleChange}
          required
        />
        <button type="submit" className="btn btn-primary">
          Update Destination
        </button>
      </form>
    </div>
  );
};

export default EditDestination;
