import React, { useState, useEffect } from "react";
import { Navbar, Nav } from "react-bootstrap";

const MyNavbar = () => {
  // Renamed component to avoid conflict
  const [navbarColor, setNavbarColor] = useState("transparent");

  const handleScroll = () => {
    if (window.scrollY > 50) {
      setNavbarColor("#343a40"); // Dark color on scroll
    } else {
      setNavbarColor("rgba(255, 255, 255, 0.3)"); // Transparent at top
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Navbar
      expand="lg"
      style={{ backgroundColor: navbarColor, transition: "0.4s" }}
      fixed="top"
    >
      <Navbar.Brand href="#" className="text-blue">
        My Website
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" className="border-blue" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="ms-auto">
          <Nav.Link href="#" className="text-black">
            Home
          </Nav.Link>
          <Nav.Link href="#" className="text-blue">
            About
          </Nav.Link>
          <Nav.Link href="#" className="text-blue">
            Contact
          </Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default MyNavbar;
