import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddDestination = () => {
  const [destination, setDestination] = useState({
    destinationName: "",
    district: "",
    province: "",
    category: "",
    description: "",
    keyHighlights: ["", "", "", "", ""], // Predefine array for key highlights
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setDestination({ ...destination, [e.target.name]: e.target.value });
  };

  const handleHighlightChange = (index, value) => {
    const newHighlights = [...destination.keyHighlights];
    newHighlights[index] = value;
    setDestination({ ...destination, keyHighlights: newHighlights });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/destinations", {
        ...destination,
        keyHighlights: destination.keyHighlights.filter(
          (highlight) => highlight.trim() !== ""
        ),
      });
      navigate("/");
    } catch (error) {
      console.error("Error adding destination:", error);
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center vh-100">
      <form
        className="p-4 border rounded shadow bg-light"
        style={{ width: "900px" }}
        onSubmit={handleSubmit}
      >
        <h3>Add Destination</h3>

        <div className="row">
          <div className="form-group col-md-6">
            <label htmlFor="destinationName">Destination Name</label>
            <input
              type="text"
              className="form-control"
              id="destinationName"
              name="destinationName"
              value={destination.destinationName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group col-md-6">
            <label htmlFor="district">District</label>
            <input
              type="text"
              className="form-control"
              id="district"
              name="district"
              value={destination.district}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="form-group col-md-6 mt-3">
            <label htmlFor="province">Province</label>
            <input
              type="text"
              className="form-control"
              id="province"
              name="province"
              value={destination.province}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group col-md-6 mt-3">
            <label htmlFor="category">Category</label>
            <select
              className="form-control"
              id="category"
              name="category"
              value={destination.category}
              onChange={handleChange}
              required
            >
              <option value="" disabled>
                -- Select Category --
              </option>
              <option value="Adventure Travel">Adventure Travel</option>
              <option value="Cultural Travel">Cultural Travel</option>
              <option value="Luxury Travel">Luxury Travel</option>
              <option value="Religious Travel">Religious Travel</option>
            </select>
          </div>
        </div>

        <div className="form-group mt-3">
          <label htmlFor="description">Description</label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            rows="3"
            value={destination.description}
            onChange={handleChange}
            required
          ></textarea>
        </div>

        <h6 className="mt-3">Key Highlights of Destination</h6>
        <div className="row">
          {destination.keyHighlights.map((highlight, index) => (
            <div className="form-group col-md-6 mt-3" key={index}>
              <input
                type="text"
                className="form-control"
                value={highlight}
                onChange={(e) => handleHighlightChange(index, e.target.value)}
                required
              />
            </div>
          ))}
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddDestination;
