import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Home = () => {
  // Internal CSS styles
  const styles = {
    videoContainer: {
      position: "relative",
      width: "100vw", // Full width
      height: "100vh", // Full height
      overflow: "hidden",
    },
    video: {
      width: "100%",
      height: "100%",
      objectFit: "cover", // Ensures the video covers the whole area
    },
    overlay: {
      position: "absolute",
      top: "0",
      left: "0",
      width: "100%",
      height: "100%",
      backgroundColor: "rgba(0, 0, 0, 0.5)", // Semi-transparent overlay
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      flexDirection: "column",
      color: "white",
      textAlign: "center",
      padding: "20px",
    },
    heading: {
      fontSize: "3rem",
      fontWeight: "bold",
      marginBottom: "10px",
    },
    subText: {
      fontSize: "1.5rem",
      marginBottom: "20px",
    },
    button: {
      backgroundColor: "#ff6600",
      color: "white",
      padding: "10px 20px",
      border: "none",
      borderRadius: "5px",
      fontSize: "1.2rem",
      cursor: "pointer",
      transition: "0.3s",
    },
    buttonHover: {
      backgroundColor: "#ff4500",
    },
  };

  return (
    <div style={styles.videoContainer}>
      {/* Video Background */}
      <video style={styles.video} autoPlay loop muted>
        <source
          src="https://www.w3schools.com/html/mov_bbb.mp4"
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>

      {/* Overlay Content */}
      <div style={styles.overlay}>
        <h1 style={styles.heading}>Welcome to TravelMate</h1>
        <p style={styles.subText}>Discover new adventures with us</p>
        <button
          style={styles.button}
          onMouseOver={(e) =>
            (e.target.style.backgroundColor =
              styles.buttonHover.backgroundColor)
          }
          onMouseOut={(e) =>
            (e.target.style.backgroundColor = styles.button.backgroundColor)
          }
        >
          Explore Now
        </button>
      </div>
    </div>
  );
};

export default Home;
