import React, { useEffect, useState } from "react";
import axios from "axios";
import { Card, Button, Container, Row, Col, Spinner, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const TourList = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("/api/tours")
      .then((res) => {
        setTours(res.data.tours || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching tours", err);
        setError("Failed to load tours. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <Container className="mt-5">
      <h2 className="mb-4">Available Tours</h2>

      {loading && (
        <div className="text-center my-5">
          <Spinner animation="border" variant="primary" />
        </div>
      )}

      {error && <Alert variant="danger">{error}</Alert>}

      {!loading && tours.length === 0 && (
        <Alert variant="info">No tours available right now.</Alert>
      )}

      <Row>
        {tours.map((tour) => (
          <Col md={4} key={tour._id} className="mb-4">
            <Card>
              <Card.Img
                variant="top"
                src={`/uploads/${tour.mainImage}`}
                onError={(e) => {
                  e.target.src = "/images/tou.png"; // fallback image
                }}
              />
              <Card.Body>
                <Card.Title>{tour.title}</Card.Title>
                <Card.Text>{tour.description}</Card.Text>
                <Button variant="primary" onClick={() => navigate(`/tours/${tour._id}/book`)}>
                  Book Now
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default TourList;
