import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const DestinationList = () => {
  const [destinations, setDestinations] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/destinations")
      .then((response) => setDestinations(response.data.destinations))
      .catch((error) => console.error("Error fetching destinations:", error));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/destinations/${id}`);
      setDestinations(destinations.filter((dest) => dest._id !== id));
    } catch (error) {
      console.error("Error deleting destination:", error);
    }
  };

  return (
    <div
      className="d-flex align-items-center justify-content-center vh-200 "
      style={{ height: "350px", marginTop: "10px" }}
    >
      <div>
        <div class="row">
          <div class="col-md-3">
            <Link to="/add" className="btn btn-primary mb-3">
              Add Destination
            </Link>
          </div>
          <div class="col-md-9">
            <div class="input-group">
              <div class="form-outline" data-mdb-input-init>
                <input type="search" id="form1" class="form-control" />
                <label class="form-label" for="form1">
                  Search
                </label>
              </div>
              <button
                type="button"
                class="btn btn-primary"
                data-mdb-ripple-init
              >
                <i class="fas fa-search"></i>
              </button>
            </div>
          </div>
        </div>
        <table
          className="table-responsive-md table-hover table-bordered"
          style={{ width: "900px" }}
        >
          <thead>
            <tr>
              <th class="px-3 py-3">Name</th>
              {/* <th>District</th>
            <th>Province</th>*/}
              <th class="px-3 py-3">Category</th>
              {/* <th>Description</th>
            <th>Key Highlights</th>*/}
              <th class="px-3 py-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {destinations.map((dest) => (
              <tr key={dest._id}>
                <td>{dest.destinationName}</td>
                {/*<td>{dest.district}</td>*/}
                {/* <td>{dest.province}</td>*/}
                <td>{dest.category}</td>
                {/*<td>{dest.description}</td>*/}
                {/*<td>{dest.keyHighlights.join(", ")}</td>*/}
                <td>
                  <Link
                    to={`/edit/${dest._id}`}
                    className="btn btn-warning me-2"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => handleDelete(dest._id)}
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DestinationList;
