import React from "react";
import { useEffect, useState } from "react";

import { useNavigate, useParams } from "react-router-dom";

function EditDestination() {
  const [input, setInputs] = useState({});
  const history = useNavigate();
  const id = useParams().id;

  useEffect(() => {
    const fetchHandler = async () => {
      await axios
        .get(`http://localhost:5000/api/destinations/${id}`)
        .then((res) => res.data)
        .then((data) => setInputs(data.user));
    };
    fetchHandler();
  }, [id]);

  const sendRequest = async () => {
    await axios
      .put(`http://localhost:5000/api/destinations/${id}`, {
        destinationName: String(input.destinationName),
        district: String(inputs.gmail),
        province: String(inputs.province),
        category: String(inputs.category),
        description: String(inputs.description),
        keyHighlights: String(inputs.keyHighlights),
      })
      .then((res) => res.data);
  };
  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(inputs);
    sendRequest().then(() => history("/destination/list"));
  };

  return (
    <div
      className="d-flex align-items-center justify-content-center vh-200 "
      style={{ height: "350px", marginTop: "250px" }}
    >
      <form
        className="p-4 border rounded shadow bg-light"
        style={{ marginLeft: "-1250px", width: "900px" }}
        onSubmit={handleSubmit}
      >
        <h3>Add Destinations</h3>
        <div className="row">
          <div className="form-group col-md-6">
            <label htmlFor="destination-name">Destination Name</label>
            <input
              type="text"
              className="form-control"
              name="destinationName"
              id="destination-name"
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group col-md-6">
            <label htmlFor="district">District</label>
            <input
              type="text"
              className="form-control"
              name="district"
              id="district"
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="form-group col-md-6 mt-3">
            <label htmlFor="province">Province</label>
            <input
              type="text"
              className="form-control"
              name="province"
              id="province"
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group col-md-6 mt-3">
            <label htmlFor="category">Category Select</label>
            <select
              className="form-control"
              name="category"
              id="category"
              onChange={handleChange}
              required
            >
              <option value="" disabled selected>
                -- Select Category --
              </option>
              <option value="1">Adventure Travel</option>
              <option value="2">Cultural Travel</option>
              <option value="3">Luxury Travel</option>
              <option value="4">Religious Travel</option>
            </select>
          </div>
        </div>

        <div className="form-group mt-3">
          <label htmlFor="description">Description</label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            rows="3"
            onChange={handleChange}
            required
          ></textarea>
        </div>

        <h6 className="mt-3">Key Highlights of Destination</h6>
        <div className="row">
          {[...Array(5)].map((_, index) => (
            <div className="form-group col-md-6 mt-3" key={index}>
              <input
                type="text"
                className="form-control"
                id={`highlight${index + 1}`}
                onChange={handleChange}
                required
              />
            </div>
          ))}
        </div>

        <h6 className="mt-3">Main Image</h6>
        <div className="form-group">
          <input type="file" className="form-control-file" id="mainImage" />
        </div>

        <h6 className="mt-3">Sub Images</h6>
        <div className="row">
          {[...Array(3)].map((_, index) => (
            <div className="form-group col-md-4" key={index}>
              <input
                type="file"
                className="form-control-file"
                id={`images${index + 1}`}
              />
            </div>
          ))}
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Submit
        </button>
      </form>
    </div>
  );
}

export default EditDestination;
