import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const DestinationList = () => {
  const [destinations, setDestinations] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/destinations")
      .then((response) => setDestinations(response.data.destinations))
      .catch((error) => console.error("Error fetching destinations:", error));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/destinations/${id}`);
      setDestinations(destinations.filter((dest) => dest._id !== id));
    } catch (error) {
      console.error("Error deleting destination:", error);
    }
  };

  return (
    <div>
      <Link to="/add" className="btn btn-primary mb-3">
        Add Destination
      </Link>
      <table className="table-responsive-md table-hover table-bordered">
        <thead>
          <tr>
            <th>Name</th>
            <th>District</th>
            <th>Province</th>
            <th>Category</th>
            <th>Description</th>
            <th>Key Highlights</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {destinations.map((dest) => (
            <tr key={dest._id}>
              <td>{dest.destinationName}</td>
              {/*<td>{dest.district}</td>*/}
              {/* <td>{dest.province}</td>*/}
              <td>{dest.category}</td>
              <td>{dest.description}</td>
              <td>{dest.keyHighlights.join(", ")}</td>
              <td>
                <Link to={`/edit/${dest._id}`} className="btn btn-warning me-2">
                  Edit
                </Link>
                <button
                  onClick={() => handleDelete(dest._id)}
                  className="btn btn-danger"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DestinationList;
