import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");
  const [step, setStep] = useState(1); // 1 for login, 2 for OTP verification
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.message);
        setStep(2); // Move to OTP verification step
      } else {
        const errorData = await response.json();
        setMessage(
          errorData.message || "Invalid credentials. Please try again."
        );
      }
    } catch (error) {
      console.error("Login error:", error);
      setMessage("Something went wrong during login. Please try again.");
    }
  };

  const handleOtpVerification = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ otp }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("token", data.token); // Store JWT token on successful login
        navigate("/admin/dashboard", { replace: true }); // Redirect to admin dashboard
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || "Invalid OTP. Please try again.");
      }
    } catch (error) {
      console.error("OTP verification error:", error);
      setMessage(
        "Something went wrong during OTP verification. Please try again."
      );
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {step === 1 ? (
        <>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </>
      ) : (
        <>
          <p>Enter the OTP sent to your email</p>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleOtpVerification}>Verify OTP</button>
        </>
      )}
      <p>{message}</p> {/* This will display any error/success message */}
    </div>
  );
};

export default Login;
